public class GuessingGame {
    private Scanner reader;
    public GuessingGame() {
        // use only this scanner, othervise the tests do not work
        this.reader = new Scanner(System.in);
    }
    public void play(int lowerLimit, int upperLimit) {
        instructions(lowerLimit, upperLimit);
        // write the guessing logic here
        while (lowerLimit < upperLimit) {
            int middle = average(lowerLimit, upperLimit);
            if (isGreaterThan(middle)) {
                lowerLimit = middle + 1;
            } else {
                upperLimit = middle;
            }
        }
        System.out.println("The number you're thinking is " + lowerLimit);
    }
    // implement here the methods isGreaterThan and average
    public boolean isGreaterThan(int luku) {
        System.out.println("Is your number greater than " + luku + "? (y/n)");
        String answer = this.reader.nextLine();
        return answer.equals("y");
    }
    public int average(int first, int second) {
        return (first + second) / 2;
    }
    public void instructions(int lowerLimit, int upperLimit) {
        int maxQuestions = howManyTimesHalvable(upperLimit - lowerLimit);
        System.out.println("Think of a number between " + lowerLimit + "..." + upperLimit + ".");
        System.out.println("I promise you that I can guess the number you are thinking with " + maxQuestions + " questions.");
        System.out.println("");
        System.out.println("Next I'll present you a series of questions. Answer them honestly.");
        System.out.println("");
    }
    // an helper method:
    public static int howManyTimesHalvable(int number) {
        // we create a base two logarithm  of the given value
        // more info on logarithms can be found for example here:
        // http://www02.oph.fi/etalukio/pitka_matematiikka/kurssi8/maa8_teoria7.html
        // Below we swap the base number to base two logarithms!
        return (int) (Math.log(number) / Math.log(2)) + 1;
    }
}

